var sockjs = require('sockjs');

var log = require('oae-logger').logger('oae-activity-rest');
var OAE = require('oae-util/lib/oae');
var OaeUtil = require('oae-util/lib/util');

var ActivityAPI = require('oae-activity');
var ActivityPush = require('./internal/push');


//////////////////////
// ACTIVITY STREAMS //
//////////////////////

/**
 * Request handler to get the activity stream of the given stream ID. It will fetch the activities and send out
 * the response.
 *
 * @param  {String}    activityStreamId    The ID of the activity stream to fetch
 * @param  {Request}   req                 The express request object
 * @param  {Response}  res                 The express response object
 * @api private
 */
var _handleGetActivities = function(activityStreamId, req, res) {
    var limit = OaeUtil.getNumberParam(req.query.limit, 10, 1, 25);
    var start = req.query.start;
    ActivityAPI.getActivityStream(req.ctx, activityStreamId, 'activity', start, limit, req.query.format, function(err, activityStream) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200, activityStream);
    });
};

/**
 * @REST getActivity
 *
 * Get the activity stream for the current user
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /activity
 * @QueryParam  {string}            [format]            The desired activity stream format. Defaults to activitystreams     [activitystreams,internal]
 * @QueryParam  {number}            [limit]             The maximum number of activities to return. Default: 25
 * @QueryParam  {string}            [start]             The activity paging token from which to start fetching activities
 * @Return      {ActivityStream}                        The activity stream for the current user
 */
OAE.tenantRouter.on('get', '/api/activity', function(req, res) {
    var userId = (req.ctx.user()) ? req.ctx.user().id : null;
    _handleGetActivities(userId, req, res);
});

/**
 * @REST getActivityActivityStreamId
 *
 * Get the activity stream for a resource
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /activity/{activityStreamId}
 * @PathParam   {string}            activityStreamId    The id of the resource for which to get the activity stream
 * @QueryParam  {string}            [format]            The desired activity stream format. Defaults to activitystreams     [activitystreams,internal]
 * @QueryParam  {number}            [limit]             The maximum number of activities to return. Default: 25
 * @QueryParam  {string}            [start]             The activity paging token from which to start fetching activities
 * @Return      {ActivityStream}                        The activity stream for the specified resource
 */
OAE.tenantRouter.on('get', '/api/activity/:activityStreamId', function(req, res) {
    _handleGetActivities(req.params.activityStreamId, req, res);
});


//////////////////////////
// NOTIFICATION STREAMS //
//////////////////////////

/**
 * @REST getNotifications
 *
 * Get the notification stream for the current user
 *
 * @Server      tenant
 * @Method      GET
 * @Path        /notifications
 * @QueryParam  {string}            [format]            The desired notification stream format. Defaults to activitystreams     [activitystreams,internal]
 * @QueryParam  {number}            [limit]             The maximum number of notificiations to return. Default: 25
 * @QueryParam  {string}            [start]             The notification paging token from which to start fetching notifications
 * @Return      {ActivityStream}                        The notifications stream
 */
OAE.tenantRouter.on('get', '/api/notifications', function(req, res) {
    var limit = OaeUtil.getNumberParam(req.query.limit, 10, 1, 25);
    var userId = req.ctx.user() ? req.ctx.user().id : null;
    ActivityAPI.getActivityStream(req.ctx, userId, 'notification', req.query.start, limit, req.query.format, function(err, notificationStream) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200, notificationStream);
    });
});

/**
 * @REST postNotificationsMarkRead
 *
 * Mark all notifications for the current user as read
 *
 * @Server      tenant
 * @Method      POST
 * @Path        /notifications/markRead
 * @Return      {NotificationsRead}                     The timestamp (millis since epoch) that was persisted as the time at which the notifications were last read
 */
OAE.tenantRouter.on('post', '/api/notifications/markRead', function(req, res) {
    ActivityAPI.markNotificationsRead(req.ctx, function(err, lastReadTime) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        res.send(200, JSON.stringify({'lastReadTime': lastReadTime}));
    });
});


////////////////////////
// PUSH NOTIFICATIONS //
////////////////////////


// Add websocket support
var sockjsOptions = {
    // No-op the logging
    'log': function(severity, line) {}
};
OAE.tenantServer.sockjs = sockjs.createServer(sockjsOptions);
OAE.tenantServer.sockjs.installHandlers(OAE.tenantServer.httpServer, {'prefix': '/api/push'});

/*!
 * Listen for new websocket connections
 */
OAE.tenantServer.sockjs.on('connection', ActivityPush.registerConnection);
